// Canvas scripts for the dynamic date in logo - thanks to Paul Lewis @ https://www.html5rocks.com/en/tutorials/canvas/hidpi/
function setupCanvas(canvas){

  // Get the device pixel ratio, or 1
    var dpr = window.devicePixelRatio || 1;

  // Get the size of the canvas from its styling attributes
    var rect = canvas.getBoundingClientRect();

  // Set canvas dimensions to the CSS dimensions, then multiply by the DPR value
    canvas.width = rect.width * dpr;
    canvas.height = rect.height * dpr;

  // Get the canvas context and scale all drawing operations by DPR
    ctx = canvas.getContext('2d');
    ctx.scale(dpr, dpr);
    
    return ctx;
} 
  // Set the year (this will be done dynamically)
    var year = $("meta[name='logoYear']").attr("content");

  // Get the drawing context of the canvas, scaled to the device pixel ratio
    var ctx = setupCanvas(document.getElementById('myCanvas'));

  // Set the text font and outline style
    ctx.font = "bold 140px Arial";
    ctx.strokstyle = "white";
    ctx.fillStyle = "white";
    ctx.lineWidth = "5";
    ctx.strokeText(year, xPos, yPos);
    ctx.strokestyle = "black";
    ctx.lineWidth = "2"
    

  // Set the position of the text
    var xPos = 45;
    var yPos = 345;

  // Draw the text and then outline it.
    ctx.fillText(year, xPos, yPos);
    ctx.strokeText(year, xPos, yPos);




  // Do the same for the navCanvas

    // Get the drawing context of the canvas, scaled to the device pixel ratio
    var ctx = setupCanvas(document.getElementById('navCanvas'));

  // Set the text font and outline style
    ctx.font = "bold 25px Arial";
    ctx.strokstyle = "black";
    ctx.fillStyle = "white";
    ctx.lineWidth = "1";

  // Set the position of the text
    var xPos = 10;
    var yPos = 60;

  // Draw the text and then outline it.
    ctx.fillText(year, xPos, yPos);
    ctx.strokeText(year, xPos, yPos);


  // Do the same for the footerCanvas

    // Get the drawing context of the canvas, scaled to the device pixel ratio
    var ctx = setupCanvas(document.getElementById('footerCanvas'));

  // Set the text font and outline style
    ctx.font = "bold 25px Arial";
    ctx.fillStyle = "white";

  // Set the position of the text
    var xPos = 10;
    var yPos = 65;

  // Draw the text and then outline it.
    ctx.fillText(year, xPos, yPos);